import React, { useState, useEffect } from "react";
import styles from "./SelectHours.module.css";
import Event from "./Event";

const SelectHours = (props) => {

  const [ timeSlots , setTimeSlots ] = useState([
    {
      label : "6am",
      value : 6
    },
    {
      label : "7am",
      value : 7
    },
    {
      label : "8am",
      value : 8
    },
    {
      label : "9am",
      value : 9
    },
    {
      label : "10am",
      value : 10
    },
    {
      label : "11am",
      value : 11
    },
    {
      label : "12pm",
      value : 12
    },
    {
      label : "1pm",
      value : 13
    },
    {
      label : "2pm",
      value : 14
    },
    {
      label : "3pm",
      value : 15
    },
    {
      label : "4pm",
      value : 16
    },
    {
      label : "5pm",
      value : 17
    },
    {
      label : "6pm",
      value : 18
    },
    {
      label : "7pm",
      value : 19
    },
    {
      label : "8pm",
      value : 20
    }
  ]); 
 
  useEffect(
    () => {
      let dateFrom = new Date(props.date);
      dateFrom.setHours(6,0,0);
      dateFrom = dateFrom.toISOString();
      let dateTo = new Date(props.date);
      dateTo.setHours(20,0,0);
      dateTo = dateTo.toISOString();

      const apiURL = '/api/freeBusyData/dateFrom/' + dateFrom + '/dateTo/' + dateTo ;

      console.log(apiURL);
      fetch(apiURL).then(
        res => res.json()
        
      ).then(
        (data) => {
          console.log(data);
          
        },
        function (error){
          console.log(error);
        }
      )
    },[]
  );

  return (
    <div>
      <label>Select time slot</label>
      <div id={styles.timeSlots}>
        <ul id={styles.timeSlotUl}>
        {
          timeSlots.map(
            slot => (
             <li key={slot.value} className={styles.timeSlotLi}><button className={styles.timeSlot}> {slot.label}
              </button></li> 
            )
          )
        }
        </ul>
      </div>
      <Event />
    </div>
  );
};

export default SelectHours;
