import React, {useState} from "react";

import classes from "./SelectPlan.module.css";
import StepsPanel from "./StepsPanel";
import Calendar from 'react-calendar';
import 'react-calendar/dist/Calendar.css';

const SelectPlan = (props) => {

  const [selectedPlanValue , setSelectedPlanValue ] = useState(props.plan);

  const hourlyPlanSelectHandler = () => {
    setSelectedPlanValue('hourly');
    console.log("hourly sleectecd");

  };
  const oneDayPlanSelectHandler = () => {
    setSelectedPlanValue('oneday');
    console.log("oneday sleectecd");

  };
  const monthlyPlanSelectHandler = () => {
    setSelectedPlanValue('monthly');
    console.log("monthly sleectecd");

  };
  
  return (
    <div className="container c-row no-padding select-plan no-wrap">
    <StepsPanel />
    <div className={`container ${classes.formDiv} `}>
   <h2 className="heading-normal">Enter booking details</h2>
   <form action="#" method="post" encType="multipart/form-data">
    <label htmlFor="selectPlan">Select Plan</label>
<select id="selectPlan" defaultValue={selectedPlanValue}>
    
    <option value="hourly" onChange={hourlyPlanSelectHandler}>Hourly</option>
    <option value="oneday" onChange={oneDayPlanSelectHandler}>Full day ( 9 hours )</option>
    <option value="monthly" onChange={monthlyPlanSelectHandler}>Monthly</option>
</select>
<label htmlFor="selectDate">Choose a date</label>
<Calendar id="selectDate"/>
{
    selectedPlanValue === 'hourly' ? <div>
      <label htmlFor="selectNoHours">Select Plan</label> 
    <select id="selectNoHours">
<option value="1" selected>1 hour</option>
<option value="2">2 hours</option>
<option value="3">3 hours</option>
<option value="4">4 hours</option>
    </select>
    </div> : <h2>hello</h2>
   }
   <button type="submit" className="hover-effect" >Proceed</button>
   
   </form>
   </div>
  </div>
  
  );
};

export default SelectPlan;
