import React, { useState } from "react";

import classes from "./SelectPlan.module.css";
import StepsPanel from "./StepsPanel";

import SelectHours from "./SelectHours";
import SelectDate from "./SelectDate";
import moment from "moment";

const SelectPlan = (props) => {
  const [selectedPlanValue, setSelectedPlanValue] = useState(props.plan);
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTimeSlot1, setSelectedTimeSlot1] = useState("");
  const [selectedTimeSlot2, setSelectedTimeSlot2] = useState("");

  const planSelectHandler = (evt) => {
    if (evt.target.value === "hourly") setSelectedPlanValue("hourly");
    else if (evt.target.value === "oneday") setSelectedPlanValue("oneday");
    else setSelectedPlanValue("monthly");
  };

  const dateChangeHandler = (date) => {
    setSelectedDate(date);
  };

  const slotsClickHandler = (clickedSlot) => {
    if (selectedTimeSlot1 === "") {
      setSelectedTimeSlot1(clickedSlot);
    } else if (selectedTimeSlot2 === "") {
      setSelectedTimeSlot2(clickedSlot);
    }
  };

  const resetHandler = () => {
    setSelectedDate('');
    setSelectedTimeSlot1("");
    setSelectedTimeSlot2("");
  };

  return (
    <div className="container c-row no-padding select-plan no-wrap">
      <StepsPanel />
      <div className={`container ${classes.formDiv} `}>
        <h2 className="heading-normal">Enter booking details</h2>
        <form action="#" method="post" encType="multipart/form-data">
          <label htmlFor="selectPlan">Select Plan</label>
          <select
            id="selectPlan"
            defaultValue={selectedPlanValue}
            onChange={planSelectHandler}
          >
            <option value="hourly">Hourly</option>
            <option value="oneday">Full day ( 9 hours )</option>
            <option value="monthly">Monthly</option>
          </select>

          <SelectDate onDateChange={dateChangeHandler} />

          {selectedPlanValue === "hourly" ? (
            <SelectHours
              date={moment(selectedDate)}
              onSlotClick={slotsClickHandler}
            />
          ) : (
            <h2>hello</h2>
          )}

          <div id="selections">
            <h3>You have selected : </h3>
            <p>
              Date :{" "}
              {selectedDate
                ? moment(selectedDate).format("MMMM Do YYYY")
                : "No date selected"}
            </p>
            <p>
              Time slot : {selectedTimeSlot1} to {selectedTimeSlot2}
            </p>
            <button className={classes.reset} onClick={resetHandler}>RESET</button>
          </div>

          <button type="submit" className="hover-effect">
            Proceed
          </button>
        </form>
      </div>
    </div>
  );
};

export default SelectPlan;
