import React, { useState, useEffect } from "react";
import styles from "./SelectHours.module.css";
import Event from "./Event";

const SelectHours = (props) => {
  const timeSlotsInitialState = [
    {
      label : "6am",
      value : 6,
      disabled : false
    },
    {
      label : "7am",
      value : 7,
      disabled : false
    },
    {
      label : "8am",
      value : 8,
      disabled : false
    },
    {
      label : "9am",
      value : 9,
      disabled : false
    },
    {
      label : "10am",
      value : 10,
      disabled : false
    },
    {
      label : "11am",
      value : 11,
      disabled : false
    },
    {
      label : "12pm",
      value : 12,
      disabled : false
    },
    {
      label : "1pm",
      value : 13,
      disabled : false
    },
    {
      label : "2pm",
      value : 14,
      disabled : false
    },
    {
      label : "3pm",
      value : 15,
      disabled : false
    },
    {
      label : "4pm",
      value : 16,
      disabled : false
    },
    {
      label : "5pm",
      value : 17,
      disabled : false
    },
    {
      label : "6pm",
      value : 18,
      disabled : false
    },
    {
      label : "7pm",
      value : 19,
      disabled : false
    },
    {
      label : "8pm",
      value : 20,
      disabled : false
    }
  ];

  const [ timeSlots , setTimeSlots ] = useState(timeSlotsInitialState); 
 
 useEffect(() => {
  
  let dateFrom = new Date(props.date);
  dateFrom.setHours(6,0,0);
  dateFrom = dateFrom.toISOString();
  let dateTo = new Date(props.date);
  dateTo.setHours(20,0,0);
  dateTo = dateTo.toISOString();

  const apiURL = '/api/freeBusyData/dateFrom/' + dateFrom + '/dateTo/' + dateTo ;
  fetch(apiURL).then(
    res => res.json()
    
  ).then(
    (data) => {
      //Data is received as an array if more than 1 busy events are found. If 1 event is found an object is returned.
      let from,to,toMinutes;
      console.log(data);
      if(data.busyData !== undefined){
       
        data.busyData.forEach(element => {
          from = new Date(element.start).getHours();
          to = new Date(element.end).getHours();
          toMinutes = new Date(element.end).getMinutes();
          if(toMinutes > 0)
            to = to + 1;
          console.log("from :" + from + "to:" + to);
          
          let updatedSlots = timeSlotsInitialState;
          for (let i = from ; i <= to ; i++ ){
            let index = timeSlots.findIndex( (e) => 
            e.value === i
          );
          console.log("index is" + index);
          
          
          updatedSlots[index].disabled = true;
            
          } 
          setTimeSlots(updatedSlots);
           
        });
      }
      else{
        //No busy slots found on this date
        setTimeSlots(timeSlotsInitialState);
      }
             
    },
    function (error){
      console.log("Error : " + error);
    }
  );

 },[props.date]);



  return (
    <div>
      <label>Select time slot</label>
      <div id={styles.timeSlots}>
        <ul id={styles.timeSlotUl}>
        {
          timeSlots.map(
            slot => (
             <li key={slot.value} className={styles.timeSlotLi}><button className={styles.timeSlot} disabled={slot.disabled} selected={true}> {slot.label}
              </button></li> 
            )
          )
          
        }
        </ul>
      </div>
      <Event />
    </div>
  );
};

export default SelectHours;
