import React, { useState, useEffect } from "react";
import styles from "./SelectHours.module.css";
import moment from "moment";

const SelectHours = (props) => {
  const timeSlotsInitialState = [
    {
      label: "6am - 7am",
      value: 6,
      disabled: false,
      selected: "false",
      arrIndex : 0
    },
    {
      label: "7am - 8am",
      value: 7,
      disabled: false,
      selected: "false",
      arrIndex : 1
    },
    {
      label: "8am - 9am",
      value: 8,
      disabled: false,
      selected: "false",
      arrIndex : 2
    },
    {
      label: "9am - 10am",
      value: 9,
      disabled: false,
      selected: "false",
      arrIndex : 3
    },
    {
      label: "10am - 11am",
      value: 10,
      disabled: false,
      selected: "false",
      arrIndex : 4
    },
    {
      label: "11am - 12 am",
      value: 11,
      disabled: false,
      selected: "false",
      arrIndex : 5
    },
    {
      label: "12pm - 1pm",
      value: 12,
      disabled: false,
      selected: "false",
      arrIndex : 6
    },
    {
      label: "1pm - 2pm",
      value: 13,
      disabled: false,
      selected: "false",
      arrIndex : 7
    },
    {
      label: "2pm - 3pm",
      value: 14,
      disabled: false,
      selected: "false",
      arrIndex : 8
    },
    {
      label: "3pm - 4pm",
      value: 15,
      disabled: false,
      selected: "false",
      arrIndex : 9
    },
    {
      label: "4pm - 5pm",
      value: 16,
      disabled: false,
      selected: "false",
      arrIndex : 10
    },
    {
      label: "5pm - 6pm",
      value: 17,
      disabled: false,
      selected: "false",
      arrIndex : 11
    },
    {
      label: "6pm - 7pm",
      value: 18,
      disabled: false,
      selected: "false",
      arrIndex : 12
    },
    {
      label: "7pm - 8pm",
      value: 19,
      disabled: false,
      selected: "false",
      arrIndex : 13
    }
  ];
  const timeSlotsInitialDisabledState = [
    {
      label: "6am - 7am",
      value: 6,
      disabled: true,
      selected: "false",
      arrIndex : 0
    },
    {
      label: "7am - 8am",
      value: 7,
      disabled: true,
      selected: "false",
      arrIndex : 1
    },
    {
      label: "8am - 9am",
      value: 8,
      disabled: true,
      selected: "false",
      arrIndex : 2
    },
    {
      label: "9am - 10am",
      value: 9,
      disabled: true,
      selected: "false",
      arrIndex : 3
    },
    {
      label: "10am - 11am",
      value: 10,
      disabled: true,
      selected: "false",
      arrIndex : 4
    },
    {
      label: "11am - 12pm",
      value: 11,
      disabled: true,
      selected: "false",
      arrIndex : 5
    },
    {
      label: "12pm - 1pm",
      value: 12,
      disabled: true,
      selected: "false",
      arrIndex : 6
    },
    {
      label: "1pm - 2pm",
      value: 13,
      disabled: true,
      selected: "false",
      arrIndex : 7
    },
    {
      label: "2pm - 3pm",
      value: 14,
      disabled: true,
      selected: "false",
      arrIndex : 8
    },
    {
      label: "3pm - 4pm",
      value: 15,
      disabled: true,
      selected: "false",
      arrIndex : 9
    },
    {
      label: "4pm - 5pm",
      value: 16,
      disabled: true,
      selected: "false",
      arrIndex : 10
    },
    {
      label: "5pm - 6pm",
      value: 17,
      disabled: true,
      selected: "false",
      arrIndex : 11
    },
    {
      label: "6pm - 7pm",
      value: 18,
      disabled: true,
      selected: "false",
      arrIndex : 12
    },
    {
      label: "7pm - 8pm",
      value: 19,
      disabled: true,
      selected: "false",
      arrIndex : 13
    }
  ];
 
  const [timeSlots, setTimeSlots] = useState(timeSlotsInitialDisabledState);
  const [selectedTimeSlot1, setSelectedTimeSlot1] = useState("");
  const [selectedTimeSlot2, setSelectedTimeSlot2] = useState("");

  useEffect(() => {
    if(! isNaN(props.date)){
    
      let dateFrom = new Date(props.date);
      dateFrom.setHours(6, 0, 0);
      dateFrom = dateFrom.toISOString();
      let dateTo = new Date(props.date);
      dateTo.setHours(20, 0, 0);
      dateTo = dateTo.toISOString();
  
      const apiURL =
        "/api/freeBusyData/dateFrom/" + dateFrom + "/dateTo/" + dateTo;
      fetch(apiURL)
        .then((res) => res.json())
        .then(
          (data) => {
            //Data is received as an array if more than 1 busy events are found. If 1 event is found an object is returned.
            let from, to, toMinutes;
         //   console.log(data);
            if (data.busyData !== undefined) {
              data.busyData.forEach((element) => {
                from = new Date(element.start).getHours();
                to = new Date(element.end).getHours();
                toMinutes = new Date(element.end).getMinutes();
                if (toMinutes > 0) to = to + 1;
              //  console.log("from :" + from + "to:" + to);
  
                let updatedSlots = timeSlotsInitialState;
                for (let i = from; i <= to; i++) {
                  let index = timeSlots.findIndex((e) => e.value === i);
                //  console.log("index is" + index);
  
                  updatedSlots[index].disabled = true;
                }
                setTimeSlots(updatedSlots);
                console.log("here 1");
                
              });
            } else {
              //No busy slots found on this date
              setTimeSlots(timeSlotsInitialState);
              console.log("here 2");
            }
          },
          function (error) {
            console.log("Error : " + error);
          }
        );
    }
    
  }, [props.date]);

  const selectSlotHandler = (evt) => {
    evt.preventDefault();
    let updatedSlots = timeSlots.map(slot => {
      if(slot.arrIndex == evt.target.getAttribute('arrindex')){
        return {...slot, selected :'true'};
      }
      return slot;
    });
        
    
   // props.onSlotClick(evt.target.name);
    setTimeSlots(
      updatedSlots
    );
    if (selectedTimeSlot1 === "") {
      console.log("first slot " + evt.target.name);
      setSelectedTimeSlot1(evt.target.name);
    } else if (selectedTimeSlot2 === "") {
      console.log("Second slot " + evt.target.name);
      setSelectedTimeSlot2(evt.target.name);
    }
    console.log("here 3");

  };

  const resetHandler = (evt) => {
    evt.preventDefault();
    setSelectedTimeSlot1("");
    setSelectedTimeSlot2("");
  };

  return (
    <div>
      <label>Select time slot</label>
      <div id={styles.timeSlots}>
        <ul id={styles.timeSlotUl}>
          {
            props.date ? 
              timeSlots.map((slot) => (
                <li key={slot.value} className={styles.timeSlotLi}>
                  <button
                    id={slot.value}
                    arrindex={slot.arrIndex} 
                    name={slot.label}
                    className={styles.timeSlot}
                    disabled={slot.disabled}
                    selectedslot={slot.selected}
                    onClick={selectSlotHandler}
                  >
                    {slot.label}
                  </button>
                </li>
              )) 
              : ''
            
          }
        </ul>
       
      </div>
      
      <button className={styles.reset} onClick={resetHandler}>RESET TIME-SLOTS</button>
            <div id="selections">
            <h3>You have selected : </h3>
            <p>
              Date :
              {props.date
                ? moment(props.date).format("MMMM Do YYYY")
                : "No date selected"}
            </p>
            <p>
              Time slot : {selectedTimeSlot1} to {selectedTimeSlot2}
            </p>
            
          </div>
     
    </div>
  );
};

export default SelectHours;
