import React, { useState, useEffect } from "react";

import Event from "./Event";

const SelectHours = (props) => {
  const express = require("express");
  const { google } = require("googleapis");

  const app = express();

  const SCOPES = "https://www.googleapis.com/auth/calendar.readonly";
  const GOOGLE_PRIVATE_KEY = process.env.REACT_APP_GOOGLE_PRIVATE_KEY;
  const GOOGLE_CLIENT_EMAIL = process.env.REACT_APP_GOOGLE_CLIENT_EMAIL;
  const GOOGLE_PROJECT_NUMBER = process.env.REACT_APP_GOOGLE_PROJECT_NUMBER;
  const GOOGLE_CALENDAR_ID = props.env.REACT_APP_CALENDAR_ID;

  const [events, setEvents] = useState([]);
  

  const getEvents = (calendarID, apiKey) => {
    const jwtClient = new google.auth.JWT(
      GOOGLE_CLIENT_EMAIL,
      null,
      GOOGLE_PRIVATE_KEY,
      SCOPES
    );

    const calendar = google.calendar({
      version: "v3",
      project: GOOGLE_PROJECT_NUMBER,
      auth: jwtClient,
    });

    app.get("/", (req, res) => {
      calendar.events.list(
        {
          calendarId: GOOGLE_CALENDAR_ID,
          timeMin: new Date().toISOString(),
          maxResults: 10,
          singleEvents: true,
          orderBy: "startTime",
        },
        (error, result) => {
          if (error) {
            res.send(JSON.stringify({ error: error }));
          } else {
            if (result.data.items.length) {
              res.send(JSON.stringify({ events: result.data.items }));
            } else {
              res.send(
                JSON.stringify({ message: "No upcoming events found." })
              );
            }
          }
        }
      );
    });
  };

  useEffect(() => {
    const events = getEvents(calendarID, apiKey);
    app.listen(3000, () => console.log(`App listening on port 3000!`));
    setEvents(events);
  }, []);

  return (
    <div>
      <label htmlFor="numHours">Select number of hours</label>
      <select id="numHours">
        <option value="1">1 hour</option>
        <option value="2">2 hours</option>
        <option value="3">3 hours</option>
        <option value="4">4 hours</option>
      </select>
      <label htmlFor="timeSlot">Select time slot</label>
      <select id="timeSlot">
        <option value="2">6 am - 8 am</option>
        <option value="2">6 am - 8 am</option>
      </select>

      <ul>
        {events?.map((event) => (
          <li key={event.id} className="flex justify-center">
            <Event description={event.summary} />
          </li>
        ))}
      </ul>
    </div>
  );
};

export default SelectHours;
