import React, {useState} from "react";

import classes from "./PersonalInfo.module.css";
import StepsPanel from "./StepsPanel";

const PersonalInfo = () => {


  
  return (
  <div className="container c-row no-padding personal-info no-wrap">
    <StepsPanel />
   <div className={`container ${classes.formDiv} `}>
    <h2 className="heading-normal">Enter personal details</h2>
    <form action="#" method="post" encType="multipart/form-data">
    
    <div id="nameSection">
      <label htmlFor="Name">Name</label>
      <input type="text" id="name" placeholder="Jane Doe" required pattern="/[A-Za-z]/" title="Please enter a valid name without numbers or special characters."/>
    </div>
    <div id="emailSection">
      <label htmlFor="email">E-mail</label>
      <input type="email" id="email" placeholder="janedoe@gmail.com" required pattern="[a-z0-9]+@[a-z]+\.[a-z]{2,3}" title="Please enter a valid e-mail address."/>
    </div>
    <div id="mobileSection">
      <label htmlFor="mobile">Mobile Number</label>
      <input type="tel"  id="mobile" placeholder="+91 123 456 7890"/>
    </div>
    <button type="submit">Proceed</button>
  </form>

    </div>
    
  </div>
  );
};

export default PersonalInfo;
