import React, { useState } from "react";
import styles from "./SelectHours.module.css";
import Event from "./Event";

const SelectHours = (props) => {

  const [ timeSlots , setTimeSlots ] = useState([
    {
      label : "6am",
      value : 6,
      disabled : false
    },
    {
      label : "7am",
      value : 7,
      disabled : false
    },
    {
      label : "8am",
      value : 8,
      disabled : false
    },
    {
      label : "9am",
      value : 9,
      disabled : false
    },
    {
      label : "10am",
      value : 10,
      disabled : false
    },
    {
      label : "11am",
      value : 11,
      disabled : false
    },
    {
      label : "12pm",
      value : 12,
      disabled : false
    },
    {
      label : "1pm",
      value : 13,
      disabled : false
    },
    {
      label : "2pm",
      value : 14,
      disabled : false
    },
    {
      label : "3pm",
      value : 15,
      disabled : false
    },
    {
      label : "4pm",
      value : 16,
      disabled : false
    },
    {
      label : "5pm",
      value : 17,
      disabled : false
    },
    {
      label : "6pm",
      value : 18,
      disabled : false
    },
    {
      label : "7pm",
      value : 19,
      disabled : false
    },
    {
      label : "8pm",
      value : 20,
      disabled : false
    }
  ]); 
 
 

  const getTimeSlots = () => {

console.log("here")    
    let dateFrom = new Date(props.date);
    dateFrom.setHours(6,0,0);
    dateFrom = dateFrom.toISOString();
    let dateTo = new Date(props.date);
    dateTo.setHours(20,0,0);
    dateTo = dateTo.toISOString();

    const apiURL = '/api/freeBusyData/dateFrom/' + dateFrom + '/dateTo/' + dateTo ;

    //console.log(apiURL);
    fetch(apiURL).then(
      res => res.json()
      
    ).then(
      (data) => {
        //Data is received as an array is more than 1 busy events are found. If 1 event is found an object is returned.
        let from,to,toMinutes;
        if(data.busyData !== undefined){
         
          data.busyData.forEach(element => {
            from = new Date(element.start).getHours();
            to = new Date(element.end).getHours();
            toMinutes = new Date(element.end).getMinutes();
            if(toMinutes > 0)
              to = to + 1;
            console.log("from :" + from + "to:" + to);
            
           
            for (let i = from ; i <= to ; i++ ){
              let index = timeSlots.findIndex( (e) => 
              e.value === i
            );
            console.log("index is" + index);
            let updatedSlots = timeSlots;
            
            updatedSlots[index].disabled = true;
              setTimeSlots(updatedSlots);
              console.log(timeSlots);
            } 
          });
        }
               
      },
      function (error){
        console.log("Error : " + error);
      }
    );

    return(
      timeSlots.map(
        slot => (
         <li key={slot.value} className={styles.timeSlotLi}><button className={styles.timeSlot} disabled={slot.disabled}> {slot.label}
          </button></li> 
        )
      )
    )
  };

  return (
    <div>
      <label>Select time slot</label>
      <div id={styles.timeSlots}>
        <ul id={styles.timeSlotUl}>
        {
          getTimeSlots()
          
        }
        </ul>
      </div>
      <Event />
    </div>
  );
};

export default SelectHours;
