import React, { useState, useEffect } from "react";
import styles from "./SelectHours.module.css";
import Event from "./Event";

const SelectHours = (props) => {
  const timeSlots = [
    "6am",
    "7am",
    "8am",
    "9am",
    "10am",
    "11am",
    "12pm",
    "1pm",
    "2pm",
    "3pm",
    "4pm",
    "5pm",
    "6pm",
    "7pm",
    "8pm",
  ];

  return (
    <div>
      <label htmlFor="numHours">Select number of hours</label>
      <select id="numHours">
        <option value="1">1 hour</option>
        <option value="2">2 hours</option>
        <option value="3">3 hours</option>
        <option value="4">4 hours</option>
      </select>
      <label htmlFor="timeSlot">Select time slot</label>
      <select id="timeSlot">
        <option value="2">6 am - 8 am</option>
        <option value="2">6 am - 8 am</option>
      </select>
      <div id={styles.timeSlots}>
        <ul id={styles.timeSlotUl}>
        {
          timeSlots.map(
            slot => (
             <li key={slot} className={styles.timeSlotLi}><button className={styles.timeSlot}> {slot}
              </button></li> 
            )
          )
        }
        </ul>
      </div>
      <Event />
    </div>
  );
};

export default SelectHours;
