import React , {useState} from 'react';
import Calendar from "react-calendar";
import moment from 'moment';
import styles from './MonthlyBooking.module.css';


const MonthlyBooking = (props) => {
    const [dates, setDates] = useState(["",""]);
     

    const dateChangeHandler = (value) => {
        //setDates(value);
        
        
        props.onDateRangeSelect(value);
    };

    const sendEnquiryClickHandler = (evt) => {
       
        
        setDates(["",""]);
    };

    return (
        <>
         <label htmlFor="selectMonth">Please select your preferred from and to dates</label>
    <Calendar id="selectMonth" minDate={new Date()} selectRange onChange={dateChangeHandler}/>
    
  
        </>
    );
};

export default MonthlyBooking;
