import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import moment from 'moment';


const SelectDate = (props) => {

    const [selectedDate , setSelectedDate ] = useState(new Date());

    const dateChangeHandler = (evt) => {
        setSelectedDate(evt);
       props.onDateChange(evt);
    };

  return (
    <>
    <label htmlFor="selectDate">Choose a date</label>
    <Calendar id="selectDate" minDate={new Date()} value={selectedDate} onChange={dateChangeHandler} />
    <p>Current selected date is {moment(selectedDate).format('MMMM Do YYYY')}</p>
    </>
  );
};

export default SelectDate;
