import React , {useState} from 'react';
import Calendar from "react-calendar";
import moment from 'moment';
import styles from './MonthlyBooking.module.css';


const MonthlyBooking = (props) => {
    const [dates, setDates] = useState(["",""]);
    const [message, setMessage] = useState(""); 

    const dateChangeHandler = (value) => {
        setDates(value);
        setMessage("You have selected " + moment(value[0]).format("MMMM Do YYYY") + " to " + moment(value[1]).format("MMMM Do YYYY"));
      //  console.log(value);
        props.onDateRangeSelect(value);
    };

    const sendEnquiryClickHandler = (evt) => {
       
        setMessage("Thank you. We have received your enquiry and we will get in touch with you within 1 business day :)");
        setDates(["",""]);
    };

    return (
        <>
         <label htmlFor="selectMonth">Please select your preferred from and to dates</label>
    <Calendar id="selectMonth" minDate={new Date()} selectRange onChange={dateChangeHandler}/>
    {message.length > 0 ? ( <div id={styles.selections}>
     <h3>{message}</h3>
    </div>) : ''}
  
        </>
    );
};

export default MonthlyBooking;
